mapper.select:

where d.del_flag = '0' and d.company_id = #{companyId}

  AND parent_id = #{sysDept.parentId}


  AND dept_name like concat('%', #{sysDept.deptName}, '%')


  AND status = #{sysDept.status}

order by d.parent_id, d.order_num

mapper.update:
update system_dept
parent_id = #{dept.parentId}, dept_name = #{dept.deptName}, ancestors = #{dept.ancestors}, order_num = #{dept.orderNum}, leader = #{dept.leader}, phone = #{dept.phone}, email = #{dept.email}, status = #{dept.status}, update_by = #{dept.updateBy}, update_time = sysdate()
where dept_id = #{dept.deptId} and company_id = #{companyId}

update system_dept set ancestors =

  when #{item.deptId} then #{item.ancestors}

where company_id = #{companyId} and dept_id in

  #{item.deptId}

update system_dept
status = #{dept.status}, update_by = #{dept.updateBy}, update_time = sysdate()
where dept_id in ('${dept.ancestors}') and company_id = #{companyId}
