mapper.select:


    
        AND menu_name like concat('%', #{menu.menuName}, '%')
    
    
        AND visible = #{menu.visible}
    
    
        AND status = #{menu.status}
    
    
        AND type = #{menu.type}
    
    
    
        AND client_type = #{menu.clientType}
    

order by parent_id, order_num


where m.menu_name=#{menuName} and m.parent_id = #{parentId}

    AND type = #{type}


    AND client_type = #{clientType}

mapper.update:
update system_menu
menu_name = #{menu.menuName}, parent_id = #{menu.parentId}, order_num = #{menu.orderNum}, path = #{menu.path}, component = #{menu.component}, is_frame = #{menu.isFrame}, menu_type = #{menu.menuType}, visible = #{menu.visible}, status = #{menu.status}, perms = #{menu.perms}, icon = #{menu.icon}, remark = #{menu.remark}, update_by = #{menu.updateBy}, update_time = sysdate()
where menu_id = #{menu.menuId}
