mapper.update:
update system_role r
r.role_name = #{role.roleName}, r.role_code = #{role.roleCode}, r.role_sort = #{role.roleSort}, r.status = #{role.status},
where r.role_id = #{role.roleId} and r.company_id = #{companyId}

mapper.select:
select r.role_id,r.role_name,r.role_code,r.role_sort,r.create_by,r.create_time,r.status,r.company_id
from system_role r
where r.role_id = #{roleId} and r.role_code = #{roleCode} and r.company_id = #{companyId}
