mapper.select:
select u.user_id,u.user_name,u.nick_name,u.email,u.phone,u.sex,u.avatar,u.status,u.password,u.dept_id,d.dept_name,u.create_time,p.post_id,u.company_id,p.post_name from system_user u left join system_dept d ON u.dept_id = d.dept_id and u.company_id = d.company_id left join system_user_post up ON up.user_id = u.user_id and u.company_id = up.company_id left join system_post p ON p.post_id = up.post_id and p.company_id = u.company_id where u.del_flag = '0' and u.company_id = ${companyId}
AND u.user_name like concat('%', #{user.userName}, '%')
AND u.status = #{user.status}
AND u.phonenumber like concat('%', #{user.phone}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{user.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{user.endTime},'%y%m%d')
AND (u.dept_id = #{user.deptId} OR u.dept_id IN ( SELECT t.dept_id FROM system_dept t WHERE t.company_id = #{companyId} and FIND_IN_SET (#{user.deptId},ancestors) ))
AND up.post_id = #{user.postId}
order by u.user_id ASC

mapper.update:
update system_user u
u.dept_id = #{deptId}, u.user_name = #{userName}, u.nick_name = #{nickName}, u.email = #{email}, u.phone = #{phone}, u.sex = #{sex}, u.avatar = #{avatar}, u.password = #{password}, u.status = #{status},
where u.user_id = #{userId} and u.company_id = #{companyId}
