mapper.select:

 where del_flag = '0' and company_id = #{companyId}

AND post_code like concat('%', #{post.postCode}, '%')


AND status = #{post.status}


AND post_name like concat('%', #{post.postName}, '%')

mapper.update:
update system_post
post_code = #{post.postCode}, post_name = #{post.postName}, post_sort = #{post.postSort}, status = #{post.status}, remark = #{post.remark}, update_by = #{post.updateBy}, update_time = sysdate()
where post_id = #{post.postId} and company_id =#{companyId}

mapper.select:


  status = '0'  and del_flag = '0' and company_id = #{companyId}

    AND post_code like concat('%', #{sysPost.postCode}, '%')


    AND status = #{sysPost.status}


    AND post_name like concat('%', #{sysPost.postName}, '%')


order by post_sort ASC
