mapper.select:
select

from otherin


        and id = #{id,jdbcType=VARCHAR}


        and otherin_no = #{otherinNo,jdbcType=VARCHAR}


        and supplier_id = #{supplierId,jdbcType=VARCHAR}


        and warehouse_id = #{warehouseId,jdbcType=VARCHAR}


        and state = #{state,jdbcType=VARCHAR}


        and remark = #{remark,jdbcType=VARCHAR}


        and create_id = #{createId,jdbcType=VARCHAR}


        and creator = #{creator,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and update_id = #{updateId,jdbcType=VARCHAR}


        and updator = #{updator,jdbcType=VARCHAR}


        and update_time = #{updateTime,jdbcType=TIMESTAMP}


        and audit_id = #{auditId,jdbcType=VARCHAR}


        and auditor = #{auditor,jdbcType=VARCHAR}


        and audit_time = #{auditTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from otherin
where id in

    #{item}
